import React, { useState } from 'react';
import './Hero.css';

function HeroSection() {
  const [tasks, setTasks] = useState([
    { id: 1, text: "Task 1" },
    { id: 2, text: "Task 2" },
    { id: 3, text: "Task 3" },
  ]);

  const handleDragStart = (e, taskIndex) => {
    e.dataTransfer.setData("text/plain", taskIndex);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, targetIndex) => {
    e.preventDefault();
    const sourceIndex = e.dataTransfer.getData("text/plain");
    const updatedTasks = [...tasks];
    const [movedTask] = updatedTasks.splice(sourceIndex, 1);
    updatedTasks.splice(targetIndex, 0, movedTask);
    setTasks(updatedTasks);
  };

  return (
    <div className="hero-container">
      <h1>Let's explore our todo-app {'\uD83D\uDE0A'}</h1>
      <p>Add your daily tasks and get things done</p>
      <div className="tasks-container">
        {tasks.map((task, index) => (
          <div
            key={task.id}
            className="task"
            draggable
            onDragStart={(e) => handleDragStart(e, index)}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, index)}
          >
            {task.text}
          </div>
        ))}
      </div>
    </div>
  );
}

export default HeroSection;
