// export default HeroSection;
import React, { useState } from 'react';
import './Hero.css';

function HeroSection() {
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [pendingTasks, setPendingTasks] = useState([]);
  const [doneTasks, setDoneTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const handleTaskAdd = (taskType) => {
    if (newTask.trim() === '') return;

    switch (taskType) {
      case 'inProgress':
        setInProgressTasks([...inProgressTasks, newTask]);
        break;
      case 'pending':
        setPendingTasks([...pendingTasks, newTask]);
        break;
      case 'done':
        setDoneTasks([...doneTasks, newTask]);
        break;
      default:
        break;
    }

    setNewTask('');
  };

  return (
    <div className="hero-container">
      <h1 className="hero-title">
        Let's explore our todo-app <span role="img">{'\uD83D\uDE0A'}</span>
      </h1>
      <p className="hero-description">Add your daily tasks and get things done</p>

      <div className="task-cards">
        <div className="task-card">
          <h2 className="card-title">In Progress</h2>
          <ul className="task-list">
            {inProgressTasks.map((task, index) => (
              <li key={index} className="task-item">{task}</li>
            ))}
          </ul>
          <input
            className="task-input"
            type="text"
            placeholder="Add a task"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button className="add-button" onClick={() => handleTaskAdd('inProgress')}>Add Task</button>
        </div>

        <div className="task-card">
          <h2 className="card-title">Pending</h2>
          <ul className="task-list">
            {pendingTasks.map((task, index) => (
              <li key={index} className="task-item">{task}</li>
            ))}
          </ul>
          <input
            className="task-input"
            type="text"
            placeholder="Add a task"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button className="add-button" onClick={() => handleTaskAdd('pending')}>Add Task</button>
        </div>

        <div className="task-card">
          <h2 className="card-title">Done</h2>
          <ul className="task-list">
            {doneTasks.map((task, index) => (
              <li key={index} className="task-item">{task}</li>
            ))}
          </ul>
          <input
            className="task-input"
            type="text"
            placeholder="Add a task"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button className="add-button" onClick={() => handleTaskAdd('done')}>Add Task</button>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
