// import React from 'react';
// import './Hero.css';

// function HeroSection() {
//   return (
//     <div className="hero-container">
//       <h1>Let's explore our todo-app  {'\uD83D\uDE0A'} </h1>
//       <p>Add your daily tasks and get things done </p>
//     </div>
//   );
// }

// export default HeroSection;
import React, { useState } from 'react';
import './Hero.css';

function HeroSection() {
  const [inProgressTasks, setInProgressTasks] = useState([]);
  const [pendingTasks, setPendingTasks] = useState([]);
  const [doneTasks, setDoneTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const handleTaskAdd = (taskType) => {
    if (newTask.trim() === '') return;

    switch (taskType) {
      case 'inProgress':
        setInProgressTasks([...inProgressTasks, newTask]);
        break;
      case 'pending':
        setPendingTasks([...pendingTasks, newTask]);
        break;
      case 'done':
        setDoneTasks([...doneTasks, newTask]);
        break;
      default:
        break;
    }

    setNewTask('');
  };

  return (
    <div className="hero-container">
      <h1>Let's explore our todo-app {'\uD83D\uDE0A'}</h1>
      <p>Add your daily tasks and get things done</p>

      <div className="task-cards">
        <div className="task-card">
          <h2>In Progress</h2>
          <ul>
            {inProgressTasks.map((task, index) => (
              <li key={index}>{task}</li>
            ))}
          </ul>
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button onClick={() => handleTaskAdd('inProgress')}>Add Task</button>
        </div>

        <div className="task-card">
          <h2>Pending</h2>
          <ul>
            {pendingTasks.map((task, index) => (
              <li key={index}>{task}</li>
            ))}
          </ul>
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button onClick={() => handleTaskAdd('pending')}>Add Task</button>
        </div>

        <div className="task-card">
          <h2>Done</h2>
          <ul>
            {doneTasks.map((task, index) => (
              <li key={index}>{task}</li>
            ))}
          </ul>
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button onClick={() => handleTaskAdd('done')}>Add Task</button>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
