// import React from 'react';
// import './Hero.css';

// function HeroSection() {
//   return (
//     <div className="hero-container">
//       <h1>Let's explore our todo-app  {'\uD83D\uDE0A'} </h1>
//       <p>Add your daily tasks and get things done </p>
//     </div>
//   );
// }

// export default HeroSection;

import React, { useState } from 'react';
import './Hero.css';

function HeroSection() {
  const [tasks, setTasks] = useState([
    { id: 1, text: "Task 1", category: "In Progress" },
    { id: 2, text: "Task 2", category: "In Progress" },
    { id: 3, text: "Task 3", category: "Done" },
    // ... add more tasks as needed
  ]);
  const [newTaskText, setNewTaskText] = useState('');

  const handleDragStart = (e, taskIndex) => {
    e.dataTransfer.setData("text/plain", taskIndex);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, targetIndex) => {
    e.preventDefault();
    const sourceIndex = e.dataTransfer.getData("text/plain");
    const updatedTasks = [...tasks];
    const [movedTask] = updatedTasks.splice(sourceIndex, 1);
    movedTask.category = tasks[targetIndex].category;
    updatedTasks.splice(targetIndex, 0, movedTask);
    setTasks(updatedTasks);
  };

  const handleAddTask = () => {
    if (newTaskText.trim() !== '') {
      const newTask = {
        id: tasks.length + 1,
        text: newTaskText,
        category: "In Progress",
      };
      setTasks([...tasks, newTask]);
      setNewTaskText('');
    }
  };

  return (
    <div className="hero-container">
      <h1>Let's explore our todo-app {'\uD83D\uDE0A'}</h1>
      <p>Add your daily tasks and get things done</p>
      <div className="tasks-container">
        <div className="category-column">
          <h2>In Progress</h2>
          <div
            className="category"
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, 0)}
          >
            {tasks.map((task, index) => (
              task.category === "In Progress" && (
                <div
                  key={task.id}
                  className="task-card"
                  draggable
                  onDragStart={(e) => handleDragStart(e, index)}
                >
                  {task.text}
                </div>
              )
            ))}
          </div>
        </div>
        <div className="category-column">
          <h2>Done</h2>
          <div
            className="category"
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, tasks.filter(t => t.category === 'Done').length)}
          >
            {tasks.map((task, index) => (
              task.category === "Done" && (
                <div
                  key={task.id}
                  className="task-card"
                  draggable
                  onDragStart={(e) => handleDragStart(e, index)}
                >
                  {task.text}
                </div>
              )
            ))}
          </div>
        </div>
      </div>
      <div className="new-task">
        <input
          type="text"
          placeholder="Enter new task"
          value={newTaskText}
          onChange={(e) => setNewTaskText(e.target.value)}
        />
        <button onClick={handleAddTask}>Add Task</button>
      </div>
    </div>
  );
}

export default HeroSection;
